import React, {
  Component
} from 'react';
import Header from './Header';
import Order from './Order';
import Inventory from './Inventory';
import sampleFishes from '../sample-fishes';
import Fish from './Fish';

class App extends React.Component {

    state = {
      fishes: {},
      order: {}
    }

    componentWillMount() {
      this.loadSampleFishes();
    }

    addFish = fish => {
      // take a copy of the existing state
      const fishes = {
        ...this.state.fishes
      };
      // add our new fish to that fishes variable
      fishes[`fish${Date.now()}`] = fish;
      // set the new fishes obj tostate
      this.setState({
        fishes: fishes
      });
    }

    loadSampleFishes = () => {
      this.setState({
        fishes: sampleFishes
      });
    }

    addToOrder = (key) => {
      // make a copy of the state
      order = {
        ...this.state.order
      };
    }


    render() {

      // var Alert = ReactClass({
      //   render = () => {
      //     return <div className={"alert" + this.p} ></div>
      //   }
      // });
      return ( <
        div className = "catch-of-the-day" >
        <
        div className = "menu" >
        <
        Header show = "iyiola" / >
        <
        ul className = "fishes" > {
          /* dynamically passing  fishKey [fhKey] */
          Object.keys(this.state.fishes).map(fhKey => < Fish key = {
              fhKey
            }
            details = {
              this.state.fishes[fhKey]
            }
            />)
          } <
          /ul> <
          /div> <
          Order / >
          <
          Inventory addFish = {
            this.addFish
          }
          loadSampleFishes = {
            this.loadSampleFishes
          }
          />  <
          /div>
        )
      }
    }


    export default App;
